import React, {useState} from "react";
import logo from "../images/trodo_logo.svg";
import ScrollableAnchor from 'react-scrollable-anchor';



function Navbar() {

    const [nav, setNav] = useState(false);
    configureAnchors({offset: -60, scrollDuration: 500})

    const changeBackground = () =>{
        if(window.scrollY >= 50){
            setNav(true);
        }
        else{
            setNav(false);
        }
    }

    window.addEventListener('scroll', changeBackground);
    return(
        <nav className={nav ? 'nav active' : 'nav'}>
            <a href="#" className="logo">
                <img src={logo} alt="trodo logo" />
            </a>
            <input type="checkbox" className='menu-btn' id='menu-btn' />
            <label className='menu-icon' for='menu-btn'>
                <span className='nav-icon'></span>
            </label>
            <ul className="menu">
                <li><a classname="whytrodo" href="#aboutSection">Why Trodo?</a></li>
                <li><a href="#featureSection">Features</a></li>
                <li><a href="#pricingSection">Pricing</a></li>
                <li><a href="#downloadSection">Download</a></li>
            </ul>
        </nav>
    )
}

export default Navbar;
